import React, { useState } from 'react';
import axios from 'axios';

const WeatherSearch = () => {
  const [location, setLocation] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const API_KEY = 'c910ec3f3e70722b68f4406733ccac23';

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const handleLocationSearch = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(onSuccess, onError);
    } else {
      alert('Your browser does not support geolocation');
    }
  };

  const onSuccess = async (location) => {
    try {
      console.log(location.coords.latitude, location.coords.longitude);
      const latitude = location.coords.latitude;
      const longitude = location.coords.longitude;
      const api = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&units=metric&appid=${API_KEY}`;
      const response = await fetch(api);
      const data = await response.json();
      setWeatherData(data);
      console.log(data);
      // Process the weather data as needed
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  const onError = (error) => {
    console.error('Error retrieving location:', error);
  };



  return (
    <div>
      <input
        type="text"
        placeholder="Enter location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={handleLocationSearch}>Get Latitude/Longitude</button>
      <button onClick={handleSearch}>Search</button>

      {weatherData && (
        <div>
          <h2>Weather for {location}</h2>
          <p>Temperature: {weatherData.main.temp} K</p>
          <p>Humidity: {weatherData.main.humidity}%</p>
          <p>Wind Speed: {weatherData.wind.speed} m/s</p>
          <p>Description: {weatherData.weather[0].description}</p>
        </div>
      )}
    </div>
  );
};

export default WeatherSearch;
