import React, { useState } from 'react';
import axios from 'axios';

const WeatherSearch = () => {
  const [location, setLocation] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const API_KEY = 'c910ec3f3e70722b68f4406733ccac23';
  const google_Api_Key = "AIzaSyBwbTOdc6sprj3m_3AM2UfOo8XMU4hzfOQ";

  const handleLocationSearch = async () => {
    try {
      const geocodeResponse = await axios.get(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${location}&key=${google_Api_Key}`);
      const { results } = geocodeResponse.data;

      if (results.length > 0) {
        const { lat, lng } = results[0].geometry.location;
        console.log(lat);
        setLatitude(lat);
        setLongitude(lng);

        const weatherResponse = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lng}&units=metric&appid=${API_KEY}`
        );
        const weatherData = weatherResponse.data;
        setWeatherData(weatherData);
      } else {
        console.error('Location not found');
      }
    } catch (error) {
      console.error('Error fetching location data:', error);
    }
  };

  return (
    <div>
      <input type="text" value={location} onChange={(e) => setLocation(e.target.value)} />
      <button onClick={handleLocationSearch}>Search</button>
      {/* Display weather data here */}
    </div>
  );
};

export default WeatherSearch;
