import { useState } from 'react';
import axios from 'axios';

const WeatherSearch = () => {
  const [location, setLocation] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const[timeZone,setTimeZone] = useState(0);
  const API_KEY = 'c910ec3f3e70722b68f4406733ccac23';


const handleLatLon = () => {
  fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&appid=${API_KEY}`)
    .then(response => response.json())
    .then(data => {
      console.log("data"+data);
      setLatitude(data.city.coord.lat);
      setLongitude(data.city.coord.lon);
      //setWeatherData(data);
    })
    .catch(error => console.log(error));
};

const handleSearch = () => {
	 handleLatLon();
	 time();
     fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&units=metric&appid=${API_KEY}`)
    .then(response => response.json())
    .then(details => {
      console.log(details);
      console.log(details.name);
      console.log(details.main.temp);
      setWeatherData(details);
    })
    .catch(error => console.log(error));
};
const time = () =>{
	const offsetInSeconds = 19800;
	const date = new Date();
	const currentTimeInMillis = date.getTime();
	const timezoneOffsetInMillis = offsetInSeconds * 1000;
	const adjustedTimeInMillis = currentTimeInMillis + timezoneOffsetInMillis;
	const adjustedDate = new Date(adjustedTimeInMillis);
	const adjustedHours = adjustedDate.getHours();
	const adjustedMinutes = adjustedDate.getMinutes();
	const adjustedSeconds = adjustedDate.getSeconds();
	const setTimeZone = `${adjustedHours}:${adjustedMinutes}:${adjustedSeconds}`;
	console.log('Adjusted Time:', timeZone);
}

  return (
   <div>
      <input type="text" placeholder="Enter location" value={location} onChange={(e) => setLocation(e.target.value)} />
      <button onClick={handleSearch}>Search</button>
      {weatherData && (
        <div>
          <h2>Weather for {location}</h2>
          <p>Temperature: {weatherData.main.temp} K</p>
          <p>Humidity: {weatherData.main.humidity}%</p>
          <p>Wind Speed: {weatherData.wind.speed} m/s</p>
          <p>Description: {weatherData.weather[0].description}</p>
        </div>
      )}
    </div>
  );
};
export default WeatherSearch;
