import React, { useState } from 'react';
import axios from 'axios';

const WeatherSearch = () => {
  const [location, setLocation] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const API_KEY = 'c910ec3f3e70722b68f4406733ccac23';


const handleSearch = () => {
  fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location.value}&appid=${API_KEY}`)
    .then(response => response.json())
    .then(data => {
      console.log(data);
      setWeatherData(data);
    })
    .catch(error => console.log(error));
};
  return (
    <div>
      <input type="text" placeholder="Enter location" value={location} onChange={(e) => setLocation(e.target.value)} />
      <button onClick={handleSearch}>Search</button>

		  <div>
		    {weatherData?.main?.temp && <p>Temperature: {weatherData.main.temp}</p>}
		    {/* Render other weather data properties */}
		  </div>
    </div>
  );
};
export default WeatherSearch;
